    clka: IN std_logic;
    clkb: IN std_logic;
    dina: IN std_logic_VECTOR(15 downto 0);
    dinb: IN std_logic_VECTOR(15 downto 0);
    doutb: OUT std_logic_VECTOR(15 downto 0);
    ena: IN std_logic;
    enb: IN std_logic;
    ndb: IN std_logic;
    rfdb: OUT std_logic;
    rdyb: OUT std_logic;
    wea: IN std_logic;
    web: IN std_logic);
end component;



